INSERT INTO `gaming-handy` (ranking, picture, name, main_url, main_price, star, review_count, highlight,detail) VALUES (5, '../img/products/Gaming-Handy/Asus Zenfone 8.webp;', 'Asus Zenfone 8', 'https://www.amazon.de/ASUS-Zenfone-Kompaktes-Smartphone-Speicher/dp/B094RF5WDF/ref=sr_1_3?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=39KCWFHMSA854&keywords=Asus+Zenfone+8&qid=1667646660&qu=eyJxc2MiOiIyLjQ0IiwicXNhIjoiMS41MSIsInFzcCI6IjEuMzAifQ%3D%3D&sprefix=oneplus+9+pro%2Caps%2C91&sr=8-3', '583,99', 4.0, 163, 0, 'ASUS;-;-;-;Alle Träger;5G;8 GB;Erstklassige Performance mit dem neuesten Qualcomm Snapdragon 888 5G Prozessor in einem kompakten, handlichen 5,9" Gerät|ultraklares 120Hz AMOLED-Display mit einer Reaktionszeit von nur 1ms geschützt durch das robuste Corning Gorilla Glass Victus|Professionelles Dual-Kamerasystem mit 64MP Auflösung, Portrait Modus, Pro Modus für Fotos und Videos und noch viel mehr|Soundqualität vom Feinsten inklusive 3,5mm-Kopfhörerbuchse - für eine bombastische Soundqualität|Das Zenfone 8 definiert Mobilität völlig neu: ultimative Leistung in einem leichten und kompakten Gerät – einfach zu bedienen und ganz einfach in der Hosentasche');
