INSERT INTO `gaming-handy` (ranking, picture, name, main_url, main_price, star, review_count, highlight,detail) VALUES (7, '../img/products/Gaming-Handy/Black Shark 4.webp;', 'Black Shark 4', 'https://www.amazon.de/Black-Shark-Smartphone-Dreifachkamera-Global-Version/dp/B094CLDQJF/ref=sr_1_3?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=3AZWX0IXID5VX&keywords=Black+Shark+4&qid=1667646673&qu=eyJxc2MiOiIzLjkxIiwicXNhIjoiMy4xOSIsInFzcCI6IjIuNjYifQ%3D%3D&sprefix=realme+gt+neo+2%2Caps%2C96&sr=8-3', '409,00', 4.4, 199, 0, 'Black Shark;Android;Wi-Fi;WLAN;Alle Träger;5G;256 GB;Black Shark 4 [5G] - Smartphone, 12 256 GB, Display: 144 Hz 6,67 Zoll/ca. 16,9 cm, Snapdragon 870, 48 MP Dreifachkamera, Akku 4500 mAh, LPDDR5 RAM UFS3.1 Storage, Schwarz (Global-Version)');
