INSERT INTO `hifi-verstärker` (ranking, picture, name, main_url, main_price, star, review_count, highlight,detail) VALUES (5, '../img/products/Hifi-Verstärker/Monacor SA-100.webp;', 'Monacor SA-100', 'https://www.amazon.de/MONACOR-SA-100-Kompakter-Universal-Stereo-Verst%C3%A4rker-schwarz/dp/B007LAQ7D6/ref=sr_1_3?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=K3GZIC5WJ9ZO&keywords=Monacor+SA-100&qid=1667662632&sprefix=m%2Caps%2C97&sr=8-3', '139,00', 4.0, 33, 0, 'black;-;Dank des lüfterlosen Kühlkonzepts des Endstufen Amps ist ein geräuschloser Betrieb gewährleistet. So wird das Klangerlebnis nicht durch Nebengeräusche beeinträchtigt|Ein Stereo Verstärker fungiert als Bindeglied zwischen einer Audioquelle (CD-Player, Plattenspieler) und Lautsprechern um somit den bestmöglichen Sound gewährleisten zu können|Ein Stereo-Amplifier liefert das Audiosignal nahezu unverfälscht an die entsprechenden Speaker und ist damit ein Highlight für HiFi-Enthusiasten und Musik-Puristen|Der Audio Amplifier verfügt über einen Kopfhörerausgang und eine Stromversorgungsbuchse für den Betrieb oder zum Aufladen mobiler Geräte (Mobil-Telefon, Tablet, Notebook)|Der Stereo-Verstärker bietet drei Eingänge für Audiogeräte mit Line-Signalpegel (z.B. CD-Spieler, PC, Tuner), sowie einen Eingang der sich auch zum Anschluss an einen Kopfhörerausgang eignet (Best.-Nr.: 25.4120)');
