INSERT INTO `thermaltake-wasserkühlung` (ranking, picture, name, main_url, main_price, star, review_count, highlight,detail) VALUES (7, '../img/products/Thermaltake-Wasserkühlung/Thermaltake Toughliquid 360 ARGB.webp;', 'Thermaltake Toughliquid 360 ARGB', 'https://www.amazon.de/Thermaltake-TOUGHLIQUID-Liquid-Cooler-K%C3%BChler/dp/B097G62SXM/ref=sr_1_1?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=3V2B10E3TFJVA&keywords=Thermaltake+Toughliquid+360+ARGB&qid=1667045345&qu=eyJxc2MiOiIxLjAwIiwicXNhIjoiMC42NSIsInFzcCI6IjAuMDAifQ%3D%3D&sprefix=thermaltake+tough%2Caps%2C93&sr=8-1', '122,99', 5.0, 1, 0, 'Thermaltake;2.4 kg;Nein;22.3 dB;Thermaltake Germany GmbH direct;Desktop;Wasser;5 Volt;✔️Die TOUGHLIQUID 360 ARGB Sync ist ein All-In-One Flüssigkeitskühlsystem, welches 5V RGB kompatible Mainboards unterstützt und es dem Benutzer ermöglicht, die Lichteffekte mit der Software des Mainboards zu steuern|✔️Der Wasserblock ist mit einem eingebauten Controller ausgestattet, der eine einfache Steuerung des Beleuchtungsmodus, der Geschwindigkeit und der Farbe des Wasserblocks ermöglicht. Somit gehören störende Controller-Kabel der Vergangenheit an|✔️Die hochleistungsfähige Kupferplatte beschleunigt die Wärmeleitung. Die hochwertige und äußerst zuverlässige Pumpe ermöglicht eine maximale Wasserzirkulation und hält die Kupferplatte kontinuierlich kühl|✔️Der TOUGHLIQUID 360 ARGB verfügt über eine drehbare Kappe, die es ermöglicht, dass das TT-Logo immer in die richtige Richtung zeigt|✔️Die TOUGHLIQUID 360 ARGB Sync All-In-One unterstützt Intel- und AMD, so dass Benutzer verschiedener CPUs nach einer einfachen Montage schnell in den Genuss fantastischer Lichteffekte kommen. Intel : LGA 2066/2011-3/2011/1366/1200/1156/1155/ 1151/1150 AMD : AM4/AM3+/AM3/AM2+/AM2/FM2/FM1');
INSERT INTO `thermaltake-wasserkühlung` (ranking, picture, name, main_url, main_price, star, review_count, highlight,detail) VALUES (8, '../img/products/Thermaltake-Wasserkühlung/Thermaltake TH240 ARGB.webp;', 'Thermaltake TH240 ARGB', 'https://www.amazon.de/Thermaltake-TH240-Liquid-Cooler-Wasserk%C3%BChlung/dp/B085HCCRPG/ref=sr_1_1?__mk_de_DE=%C3%85M%C3%85%C5%BD%C3%95%C3%91&crid=SFS76D4QPM7X&keywords=Thermaltake+TH240+ARGB&qid=1667045350&qu=eyJxc2MiOiIxLjMxIiwicXNhIjoiMC45MiIsInFzcCI6IjAuMDAifQ%3D%3D&sprefix=%2Caps%2C69&sr=8-1', '78,90', 4.3, 67, 0, 'Thermaltake;1.5 kg;Nein;20 dB;Thermaltake Germany GmbH direct;Desktop;Wasser;5 Volt;✔️Die TH240 ist eine 240mm All-In-One Liquid Cooler, der 5V RGB kompatible Motherboards unterstützt|✔️Mit dem ARGB-Controller können Benutzer den LED-Modus, die LED-Farben und die Beleuchtungsgeschwindigkeit umschalten, wenn sie ein Motherboard ohne einen adressierbaren 5V RGB-Header verwenden. Der Benutzer kann zwischen den Modi Flow, Ripple, Pulse, Blink, Wave, Full Light, RGB Spectrum und Off mit verschiedenen Farben und LED-Geschwindigkeitsoptionen wechseln|✔️Dank des Lüfterdesigns liefert die TH240 eine bemerkenswerte Kühlleistung|✔️Der Spezielle 240mm Radiator mit großer Oberfläche sorgt für eine sehr gute Wärmeableitung|✔️Die hochwertige und zuverlässige Pumpe ermöglicht eine maximale Wasserzirkulation und hält die Kupferplatte kontinuierlich kühl. Der Kühlmittelverbrauch wird effektiv verringert; daher ist kein Nachfüllen erforderlich');
